import React, { useState } from 'react';

const ContactForm = () => {
  const [contact, setContact] = useState({
    name: '',
    email: '',
    phone: '',
    type: 'personal',
  });
  const { name, email, phone, type } = contact;
  const onChange = (e) => {};
  const onSubmit = (e) => {
    e.preventDefault();
  };
  return (
    <form onSubmit={onSubmit}>
      <h2 className='text-primary'>Add Contact</h2>
      <input
        type='text'
        placeholder='Name...'
        name={name}
        value={name}
        onChange={onChange}
      />
      <input
        type='text'
        placeholder='Email ...'
        name={email}
        value={email}
        onChange={onChange}
      />
      <input
        type='text'
        placeholder='Phone ...'
        name={phone}
        value={phone}
        onChange={onChange}
      />
      <h5>Contact Type</h5>
      <input
        type='radio'
        name={type}
        value='peronal'
        checked={type === 'personal'}
      />{' '}
      Personal {''}
      <input
        type='radio'
        name={type}
        value='professional'
        checked={type === 'professional'}
      />
      Professional{''}
      <div>
        <button className='btn btn-primary'>Add Contact</button>
      </div>
    </form>
  );
};

export default ContactForm;
