import axios from 'axios';

const setAuthToken = (token) => {
  axios.interceptors.request.use((config) => {
    if (token) {
      const finalToken = 'Bearer ' + token;
      config.headers.authorization = finalToken;
    }
    return config;
  });
};

export default setAuthToken;
